zhdw=#{zhdw},zhtime=#{zhtime},points=#{points},reward=#{reward},judgestatus=#{judgestatus}

    , fjsc=#{fjsc}


    ,systime=#{systime}

where cid=#{cid}

update cgzh set departmentopinion=#{departmentopinion},schoolopinion=#{schoolopinion},judgestatus=#{judgestatus}
where cid in
${id}

