mapper.select:
select * from hpjl where 1=1

    and department = #{department}


    and name like '%${name}%'


    and number = #{username}


    and systime >= #{start}


    and systime <= #{end}


    and judgestatus  = #{judgestatus}


    and ( judgestatus = '1' or  judgestatus = '2' or judgestatus = '3'  or judgestatus = '4' or judgestatus = '5' )


    and (   judgestatus = '2' or judgestatus = '3'   or judgestatus = '5' )

order by systime desc,department desc,judgestatus,points desc

mapper.update:
update hpjl set cgname=#{cgname},jlname=#{jlname}, dyhjr=#{dyhjr},qthjr=#{qthjr},jixdbm=#{jixdbm},zsnumber=#{zsnumber},
hjtime=#{hjtime},jlrank=#{jlrank}, remark=#{remark},points=#{points},kyjl=#{kyjl}
,judgestatus=#{judgestatus}

    , fjsc=#{fjsc}


,systime=#{systime}

where  hid=#{hid}

update hpjl set departmentopinion=#{departmentopinion},schoolopinion=#{schoolopinion},judgestatus=#{judgestatus}
where hid in
${id}

mapper.select:
select * from hpjl where 1=1

    and department = #{department}


    and number = #{number}


and judgestatus ='3'

order by systime desc,department

select
${param}
from hpjl where 1=1

    and department = #{department}


    and name like '%${name}%'


    and number = #{number}


    and systime >= #{start}


    and systime <= #{end}

and judgestatus ='3'

SELECT COUNT(1) as count,YEAR(systime) as year FROM hpjl WHERE
YEAR(systime) BETWEEN #{yearstart} AND #{yearend}
and judgestatus='3'

    and department = #{department}

GROUP BY YEAR(systime);
